import React, { useState } from "react";
import ReactDOM from "react-dom";
import { tailwind } from "tailwindcss";
import styled from "styled-components";

const DetailedView = () => {
  const [sipAmount, setSIPAmount] = useState(0);
  const [years, setYears] = useState(0);
  const [annualInterestRate, setAnnualInterestRate] = useState(0);

  const calculateSIPAmount = () => {
    const totalInvestment = sipAmount * years;
    const totalReturn = totalInvestment * (1 + annualInterestRate / 100) ** years;

    return {
      totalInvestment,
      totalReturn,
    };
  };

  return (
    <div className="container mx-auto">
      <h1 className="text-xl font-bold mb-4">SIP Calculator</h1>
      <div className="flex flex-col mb-4">
        <label for="sipAmount" className="text-sm font-medium mb-1">SIP Amount</label>
        <Input
          type="number"
          id="sipAmount"
          value={sipAmount}
          onChange={(e) => setSIPAmount(e.target.value)}
          className="w-full py-2 px-2 border border-gray-300 rounded-md"
        />
      </div>
      <div className="flex flex-col mb-4">
        <label for="years" className="text-sm font-medium mb-1">Number of Years</label>
        <Input
          type="number"
          id="years"
          value={years}
          onChange={(e) => setYears(e.target.value)}
          className="w-full py-2 px-2 border border-gray-300 rounded-md"
        />
      </div>
      <div className="flex flex-col mb-4">
        <label for="annualInterestRate" className="text-sm font-medium mb-1">Annual Interest Rate</label>
        <Input
          type="number"
          id="annualInterestRate"
          value={annualInterestRate}
          onChange={(e) => setAnnualInterestRate(e.target.value)}
          className="w-full py-2 px-2 border border-gray-300 rounded-md"
        />
      </div>
      <div className="flex justify-end">
        <button
          onClick={calculateSIPAmount}
          className="bg-blue-500 text-white py-2 px-4 rounded-md hover:bg-blue-700"
        >
          Calculate
        </button>
      </div>
      <div>
        {calculateSIPAmount() && (
          <div className="flex flex-col mb-4">
            <h2>
              Total Investment: ₹{calculateSIPAmount().totalInvestment}
            </h2>
            <h2>
              Total Return: ₹{calculateSIPAmount().totalReturn}
            </h2>
          </div>
        )}
      </div>
    </div>
  );
};

const Input = styled.input`
  font-size: 16px;
  line-height: 1.5;
  width: 100%;
  padding: 10px 12px;
  border: 1px solid #ccc;
  border-radius: 4px;
  box-sizing: border-box;
  transition: border-color 0.15s ease-in-out;

  &:focus {
    border-color: #000;
  }
`;

export default DetailedView;
