// TradingViewWidget.js

import React, { useEffect, useRef } from 'react';

let tvScriptLoadingPromise;

export default function TradingViewWidget({ stock }) {
  const onLoadScriptRef = useRef();

  useEffect(() => {
    onLoadScriptRef.current = createWidget;

    if (!tvScriptLoadingPromise) {
      tvScriptLoadingPromise = new Promise((resolve) => {
        const script = document.createElement('script');
        script.id = 'tradingview-widget-loading-script';
        script.src = 'https://s3.tradingview.com/tv.js';
        script.type = 'text/javascript';
        script.onload = resolve;

        document.head.appendChild(script);
      });
    }

    tvScriptLoadingPromise.then(() => onLoadScriptRef.current && onLoadScriptRef.current());

    return () => (onLoadScriptRef.current = null);

    function createWidget() {
        if (document.getElementById('tradingview_50c0f') && 'TradingView' in window) {
          const widget = new window.TradingView.widget({
            autosize: true,
            symbol: `NASDAQ:${stock}`,
            interval: 'D',
            timezone: 'Etc/UTC',
            theme: 'light',
            style: '1',
            locale: 'in',
            toolbar_bg: '#f1f3f6',
            enable_publishing: false,
            allow_symbol_change: true,
            container_id: 'tradingview_50c0f'
          });

          setTimeout(() => {
            const chartContainer = document.getElementsByClassName('tradingview-widget-container')[0];
            const chart = chartContainer.getElementsByClassName('tradingview-widget-container__widget')[0];
            chart.style.height = '400px'; // Set the desired height value
          }, 0);
        }
      }


  }, []);

  return (
    <div className="tradingview-widget-container">
      <div id="tradingview_50c0f"/>
      <div className="tradingview-widget-copyright">
        <a href="https://in.tradingview.com/" rel="noopener nofollow" target="_blank">
          <span className="blue-text">Track all markets on TradingView</span>
        </a>
      </div>
    </div>
  );
}
