import React, { useState } from 'react';
import axios from 'axios';

const DetailedView = () => {
  const [buying, setBuying] = useState(false);
  const [selling, setSelling] = useState(false);
  const availableBalance = 10000; // Example available balance
  const insuranceAmount = 0.3; // Insurance amount as a fraction (30%)
  const marginPercentage = 0.2; // Margin percentage as a fraction (20%)

  return (
    <div className="min-h-screen bg-black">
      <div className="flex justify-between px-5 py-3">
        <h1 className="text-green-300 font-bold text-3xl">Detailed View</h1>
