      </div>
      <div className="flex flex-col items-center justify-center">
        <h1 className="text-green-300 font-bold text-3xl p-3">Stock Details</h1>
        <p className="text-green-300 font-bold text-3xl p-3">Symbol: XYZ</p>
        <p className="text-green-300 font-bold text-3xl p-3">Opening Price: $100</p>
        <p className="text-green-300 font-bold text-3xl p-3">High Price: $120</p>
        <p className="text-green-300 font-bold text-3xl p-3">Low Price: $80</p>
        <p className="text-green-300 font-bold text-3xl p-3">Close Price: $110</p>
        <p className="text-green-300 font-bold text-3xl p-3">Available Balance: ${availableBalance}</p>
        <p className="text-green-300 font-bold text-3xl p-3">Insurance Amount: ${availableBalance * insuranceAmount}</p>
      </div>
      <div className="flex item-center justify-center mt-5">
        <button
          onClick={handleBuy}
          disabled={buying}
          className="bg-green-300 text-black font-bold p-6 rounded-xl"
        >
          {buying ? 'Buying...' : 'Buy'}
        </button>
        <button
          onClick={handleSell}
          disabled={selling}
          className="bg-green-300 text-black font-bold p-6 rounded-xl"
        >
          {selling ? 'Selling...' : 'Sell'}
        </button>
      </div>
    </div>
  );
};

export default DetailedView;
