import React, {useState} from 'react';
import axios from 'axios';

const Dashboard = () => {

    const[stock, setStock] = useState('')

    const handleChange = (e) => {
        setStock(e.target.value);
    }

    const handleSearch = () => {
        axios.get(`localhost:8000/stock/${stock}`)
        .then((res) => {
            console.log(res.data);
        })
        .catch((err) => {
            console.log(err);
        })
    }


    return (
        <div className="min-h-screen bg-black">
        <div className="flex justify-between px-5 py-3">
        <h1 className="text-green-300 font-bold text-3xl">StockSure</h1>
        <div className="bg-green-300 p-6 rounded-full ">ðŸ˜Ž</div>
        </div>
        <div className="flex flex-col items-center justify-center">
            <h1 className="text-green-300 font-bold text-3xl p-3">Dashboard</h1>
            <p className="text-green-300 font-bold text-3xl p-3">Welcome to StockSure</p>
            <div className=" text-white font-bold">Total Balance: â‚¹10000</div>
            </div>
            <div className="flex item-center justify-center mt-5 gap-2">
            <input type="text" onChange={handleChange} placeholder="Enter Stock Symbol: Eg: AAPL" className="border-none outline-gray-800 bg-gray-900 w-5/6 p-6 rounded-xl text-white focus:ring-2 focus:outline-none focus:ring-green-300"/>
            <button onClick={handleSearch} className="bg-green-300 text-black font-bold p-6 rounded-xl">Search</button>
            </div>
        </div>
    )
    }

export default Dashboard;
